import React from "react";
import { useReducer } from "react/cjs/react.development";
import { Board } from "./Board";


const reducer= (state,action)=>{
    switch (action.type){
        case 'MOVE':
            return{...state,history:state.history.concat({
                sqaures:action
            })}
    }
} 
export const Game = () => {
    const [state,dispatch] = useReducer(reducer,{
        xIsNext: true,
        history :[{sqaures:Array(9).fill(null)}],
    });
    const {isNext, history} =  state;
    const status = "Next Player is X";
    const moves = (<li><button>Start the Game</button></li>);
    const sqaures = Array(9).fill(null);
  return (
    <div className="game">
      <div className="game-board">
        <Board squares={sqaures}></Board>
      </div>
      <div className="game-info">
        <div>{status}</div>
        <ul>{moves}</ul>
      </div>
    </div>
  );
};
