import React from "react";
import { useReducer } from "react/cjs/react.development";
import { Board } from "./Board";


const reducer= (state,action)=>{
    switch (action.type){
        case 'JUMP':
            return {...state,
            xIsNext: action.payload.step%2 ===0,
            history: state.history.slice(0,action.payload)
        }
        case 'MOVE':
            return{...state,history:state.history.concat({
                sqaures:action.payload.sqaures,
            }),
            xIsNext: !state.isNext,
        };
        default:
            return state;
    }
} 
export const Game = () => {
    const [state,dispatch] = useReducer(reducer,{
        xIsNext: true,
        history :[{sqaures:Array(9).fill(null)}],
    });
    const {xIsNext, history} =  state;
    const jumpTo = (step)  =>{
        dispatch({
            type:'JUMP', payload: {step}
        });
    }
    const handleClick = (i) =>{
        const current = history[history.length - 1];
        const squares = current.squares.slice();
        const winner = calculateWinner(squares);
        if (winner || squares[i]){
            return;
        }
        sqaures[i] = xIsNext? 'X':'O';
        dispatch({type:'MOVE',payload:{sqaures}});

    }
    const current = history[history.length - 1];
    const winner = calculateWinner(current.squares);
    const status = winner?winner === 'D'?'Draw': 'Winner is ' + winner:'Next player is' + (xIsNext?'X':'O');
    const moves = history.map((step, move) =>{
        const desc = move ? 'Go to #'+ move :'Start the Game';
        return <li key={move}><button onClick={()=>jumpTo(move)}>{desc}</button></li>
    })
    const sqaures = Array(9).fill(null);
  return (
    <div className="game">
      <div className="game-board">
        <Board squares={sqaures}></Board>
      </div>
      <div className="game-info">
        <div>{status}</div>
        <ul>{moves}</ul>
      </div>
    </div>
  );
};
